        link.creat('ccss', 'pc.css')
    })
    $('#mob').click(function () {
        link.creat('ccss', 'mob.css')
    })
    $('#remove').click(function () {
        link.remove('#ccss')
    })


    var link = {
        remove: function (id) {
            $('link').remove(id);
        },
        //单例模式写法
        creat: (function () {
            var linkTag;
